create table Aeroportos (
    Sigla       char(5) NOT NULL,
    Descricao   varchar(100),
    Municipio   varchar(70) NOT NULL,
    UF          char(4),
    Pais        char(25),
    Continente  char(16)
    );



UPDATE aeroportos SET sigla=Upper(sigla);

SELECT Count(*) From Aeroportos;
